import {Pagination} from "swiper/modules";
import React, {useEffect, useState} from 'react';
import {useTelegram} from "../../../../hooks/useTelegram";
import {useNavigate} from "react-router-dom";
import style from './MoreInfo.module.scss'
import {Swiper, SwiperSlide} from "swiper/react";
import 'swiper/css/pagination';

const URL = 'https://2ae04a56-b56e-4cc1-b14a-e7bf1761ebd5.selcdn.net'

const buttonList = [[{
    name: 'Избранное',
    navigate: '/favorites',
    img: 'data:image/png;base64,',
    hueRotate:'90deg'
}, {
    name: 'История покупок',
    navigate: '/history',
    img: 'data:image/png;base64,',
    hueRotate:'90deg'
}, {
    name: 'Поддержка',
    path: 'https://t.me/gwstore_admin',
    img: 'data:image/png;base64,',
    hueRotate:'90deg'
}], [{
    name: 'Группа Vk для PlayStation',
    path: 'https://vk.com/gwstore.playstation',
    img: 'data:image/png;base64,',
    hueRotate:'90deg'
}, {
    name: 'Канал Telegram по Xbox',
    path: 'https://t.me/gwstore_xbox',
    img: 'data:image/png;base64,',
    hueRotate:'90deg'
}, {
    name: 'Группа Vk для PlayStation',
    path: 'https://vk.com/gwstore.xbox',
    img: 'data:image/png;base64,',
    hueRotate:'90deg'
}, {
    name: 'Канал Telegram по PlayStation',
    path: 'https://t.me/gwstore_playstation',
    img: 'data:image/png;base64,',
    hueRotate:'90deg'
}], [{
    name: 'Пользовательское соглашение',
    path: 'https://t.me/gwstore_faq/11',
    img: 'data:image/png;base64,',
    hueRotate:'90deg'
}, {
    name: 'Политика конфиндециальности',
    path: 'https://t.me/gwstore_faq/12',
    img: 'data:image/png;base64,',
    hueRotate:'90deg'
}, {
    name: 'Faq для PlayStation',
    path: 'https://t.me/gwstore_faq/10',
    img: 'data:image/png;base64,',
    hueRotate:'90deg'
}, {
    name: 'Faq для Xbox',
    path: 'https://t.me/gwstore_faq/9',
    img: 'data:image/png;base64,',
    hueRotate:'90deg'
}],]

const MoreInfo = () => {
    const {tg} = useTelegram();
    const navigate = useNavigate();
    const [info, setInfo] = useState([]);

    const pagination = {
        clickable: true, renderBullet: function (index, className) {
            return '<span class="' + className + '"></span>';
        },
    };

    useEffect(() => {
        fetch(`${URL}/getInfoBlock?time${new Date()}`, {
            method: 'GET', headers: {
                'Content-Type': 'application/json',
            }
        }).then(async response => {
            let answer = response.json()
            answer.then((data) => {
                setInfo(data.result)
            })
        })
    }, [])

    useEffect(() => {
        tg.BackButton.show();
        tg.onEvent('backButtonClicked', () => navigate(-1))
        return () => {
            tg.offEvent('backButtonClicked', () => navigate(-1))
        }
    }, [])

    return (<div className={style['container']}>

        <div
            style={{
                paddingBottom: String(window.innerWidth * 0.20 + tg.contentSafeAreaInset.bottom + tg.safeAreaInset.bottom + (window.screen.availHeight - window.innerHeight - (window.screen.availHeight - window.innerHeight > 0) ? window.innerWidth * 0.20 : 0) + 10) + 'px',
                paddingTop: String(tg?.contentSafeAreaInset.top + tg?.safeAreaInset.top + 10) + 'px',
            }}>
            {buttonList.map((block) => (
                <div className={style['blockButton']}>
                    {
                        block.map((button, index) => (
                            <div onClick={()=>{
                                if(typeof button.path !== 'undefined'){
                                    window.open(button.path)
                                }else{
                                    navigate(button.navigate)
                                }
                            }}>
                                <div>
                                    <div style={{backgroundImage: `url(${button.img})`, filter:`hue-rotate(${button.hueRotate})`}}/>
                                </div>
                                <div>
                                    <div>
                                        <p>{button.name}</p>
                                        <div/>
                                    </div>
                                    {index + 1 < block.length ? (<div className={style['separator']}/>) : ''}
                                </div>
                            </div>
                        ))
                    }
                </div>
            ))}


            <div className={style['title']}>Акции и спецпредложения</div>

            <div style={{width: '100vw', overflow: 'hidden', height: '46vw'}}>
                <Swiper slidesPerView={2} pagination={pagination} style={{width: '105vw', marginLeft: '-2.5vw'}}
                        modules={[Pagination]} spaceBetween={0} loop={info.length > 3}>
                    {info.map((item, index) => (<SwiperSlide key={index}>
                        <div className={style['slide']} style={{
                            webkitAnimationDelay: String(0.1 * index) + 's',
                            animationDelay: String(0.1 * index + 0.2) + 's',
                        }} onClick={() => {
                            window.open(item.path)
                        }}>
                            <div>{item.name}</div>
                            <div>{item.body}</div>
                        </div>
                    </SwiperSlide>))}
                    {info.length < 4 ? <SwiperSlide/> : ''}

                </Swiper>
            </div>
        </div>
    </div>);
};

export default MoreInfo;