import React from 'react';
import style from "./InfoBubbles.module.scss";

const psIcon = 'data:image/png;base64,'
const xboxIcon = ''
const morePeople = 'data:image/png;base64,'
const onePeople = 'data:image/png;base64,'
const russian = 'data:image/png;base64,'
const english = 'data:image/png;base64,'

const InfoBubbles = ({productData}) => {

    let bubbles = []

    if(productData.platform !== null) {
        bubbles = [...bubbles, ...(productData.platform.split(',').map(item => {
            return {label: item, icon: item.includes('PS') ? psIcon : xboxIcon}
        }))]
    }

    if(productData.numberPlayers !== null) {
        bubbles = [...bubbles, {label: productData.numberPlayers, icon: productData.numberPlayers.includes('-') ? morePeople : onePeople}]
    }

    if(productData.language !== null) {
        if(productData.language === 'На русском языке') {
            bubbles = [...bubbles, {label: 'Русский текст и озвучка', icon: russian}]
        }
        if(productData.language === 'Русские субтитры (текст)') {
            bubbles = [...bubbles, {label: 'Русский текст', icon: russian}]
        }
        if(productData.language === 'Без перевода') {
            bubbles = [...bubbles, {label: 'На английском', icon: english}]
        }
    }

    console.log(bubbles)

    return (<div className={style['infoBubbles']}>
        {bubbles.filter(item => item.label !== null).map((item, index) => {
            return (<div key={index}>
                <div style={{backgroundImage: `url(${item.icon})`}}/>
                <p>{item.label}</p>
            </div>)
        })}
    </div>);
};

export default InfoBubbles;